mapper.insert:


    SELECT MAX(TeamNo)+1 FROM Team        

INSERT 
INTO Team
        (TeamNo
        ,TeamNm
        ,ShortNm
        ,TeamLv
        ,UpperTeamNo
        ,StartDay
        ,ReferTeamNo
        ,IsUse
        ,IsLeaf
        ,TeamMemo) 
VALUES
        (#{TeamNo}
        ,#{TeamNm}
        ,#{ShortNm}
        ,#{TeamLv}
        ,#{UpperTeamNo}
        ,#{StartDay}
        ,#{ReferTeamNo}
        ,#{IsUse}
        ,#{IsLeaf}
        ,#{TeamMemo})

mapper.update:

UPDATE Team 
SET TeamNm=#{TeamNm}
    ,ShortNm=#{ShortNm}
    ,TeamLv=#{TeamLv}
    ,UpperTeamNo=#{UpperTeamNo}
    ,StartDay=#{StartDay}
    ,ReferTeamNo=#{ReferTeamNo}
    ,IsUse=#{IsUse}
    ,IsLeaf=#{IsLeaf}
    ,TeamMemo=#{TeamMemo}
WHERE TeamNo=#{TeamNo}
